sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

// Query the database for NeedIt Task records with Due date field values between now
// and in 24 hours time.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var futureNITasks = new GlideRecord('x_58872_needit_needit_task');
futureNITasks.addQuery('due_date','>=',rightNow);
gs.warn("NeedIt logging1: "+rightNow);
rightNow.addSeconds(86400);
gs.warn("NeedIt logging2: "+rightNow);
gs.warn("NeedIt logging3: "+gs.hoursAgo(-24));
futureNITasks.addQuery('due_date','<',rightNow);
//futureNITasks.addQuery('due_date','<',in24hours);
//futureNITasks.addQuery('due_date','<',gs.hoursAgo(-24));
futureNITasks.addQuery('state','!=',3);

futureNITasks.query();
// Write a log message for each overdue NeedIt Task Record
while(futureNITasks.next()){
	gs.info("NeedIt Task record with overdue between now and 24 hours from now4 = " + futureNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', futureNITasks, futureNITasks.number, gs.getUserName());
}
